        where d.comp_code = m.comp_code and  d.comp_code=k.comp_code and d.comp_code = '||''''||pcompcode||''''||' and 
        d.unit_code = m.unit and d.sup_type_code=k.sup_ty_code and d.unit_code = '||''''||punitcode||''''||' and 
