        (m.ag_type=pagencytype or pagencytype is null) and (m.ag_class=pagencyclass or pagencyclass is null) and (m.dist_code=pdistcode or pdistcode is null) and 
        (m.tehsil_taluka=ptaluka or ptaluka is null) 
        group by d.comp_code,d.unit_code,d.agcd,d.dpcd,d.billdt,d.billno,d.bill_frdt,d.bill_todt
        order by d.comp_code,d.unit_code,d.billdt,d.billno,d.agcd,d.dpcd;       
    rec_bill cur_agcd%rowtype;

    cursor cur_edtn is
    select distinct publ,edtn from cir_bill_det
        where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and billdt=rec_bill.billdt and billno=rec_bill.billno and
              agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd
        union
        select distinct publ,edtn
        from cir_unsold_dtl
        where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and 
              app_dt>=v_frdt and app_dt<=v_todt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and (nvl(apr_late_recpt,0)+nvl(apr_short_recpt,0)+nvl(apr_bnr,0)+nvl(apr_unsold,0))>0
              order by publ,edtn;
    rec_edtn cur_edtn%rowtype;
    
    cursor cur_dbksup is
        select d.comp_code comp_code,d.unit_code unit_code,d.publ publ,d.edtn edtn,d.sup_rate copy_rate,d.supdate,sum(d.sup_copy) supply_copy
        from cir_dbksup d,cir_supply_type_mast t 
        where d.comp_code=rec_bill.comp_code and d.comp_code=t.comp_code and d.unit_code=rec_bill.unit_code and 
              d.supdate=v_dt and d.sup_type_code=t.sup_ty_code and upper(t.bill_pay)='Y' and d.billagcd=rec_bill.billagcd and 
              d.billdpcd=rec_bill.billdpcd and nvl(d.sup_copy,0)>0 and d.edtn=rec_edtn.edtn
    group by d.comp_code,d.unit_code,d.publ,d.edtn,d.sup_rate,d.supdate
    order by d.comp_code,d.unit_code,d.publ,d.edtn,d.supdate;
    rec_dbksup cur_dbksup%rowtype;
    
    cursor cur_unsold is
        select comp_code,unit_code,publ,edtn,recptdt,copy_rate,sum(nvl(apr_late_recpt,0)+nvl(apr_short_recpt,0)+nvl(apr_bnr,0)+nvl(apr_unsold,0)) return_copy,
        sum(nvl(copy_amt,0)+nvl(comm_amt,0)) return_gross,sum(nvl(comm_amt,0)) return_comm
        from cir_unsold_dtl
        where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and publ=rec_edtn.publ and edtn=rec_edtn.edtn and
              app_dt=v_dt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and (nvl(apr_late_recpt,0)+nvl(apr_short_recpt,0)+nvl(apr_bnr,0)+nvl(apr_unsold,0))>0
    group by comp_code,unit_code,recptdt,publ,edtn,copy_rate
    order by comp_code,unit_code,recptdt,publ,edtn,copy_rate;
    rec_unsold cur_unsold%rowtype;
    
        v_return_copy     number;
        v_return_amt      number(14,2):=0;
        v_gross_amt       number(15,2):=0;
        v_comm_rate       number;
        v_comm_amt        number(15,2):=0;
        v_sur_rate        number;
        v_sur_amt         number(15,2):=0;    
        v_opdate          date;
        v_opbal           number(14,2):=0;
        v_billamt         number(14,2):=0;
        v_dbcramt         number(14,2):=0;
        v_dbamt           number(14,2):=0;
        v_cramt           number(14,2):=0;
        v_recamt          number(14,2):=0;   
        v_secamt          number(14,2):=0;
        v_psecamt         number(14,2):=0;
        v_csecamt         number(14,2):=0;
Begin
    v_frdt    :=to_date(pfrom_date,''''||pdateformat||'''');
    v_todt    :=to_date(ptill_date,''''||pdateformat||'''');
    v_opdate  :=cir_get_finandt(pcompcode,to_date(pfrom_date,''''||pdateformat||''''),pdateformat,'','');
    
    --delete test1;commit;
    --insert into test1(vstring,vstring2) values(' cir_pur_bill_print_p ', pbillagcd||pbilldpcd);commit; 
    delete from cir_temp_outstanding;
    delete from cir_bill_print where cur_sessionid=userenv('sessionid');
    delete from cir_bill_return_print where cur_sessionid=userenv('sessionid');
        
    open cur_agcd;
    loop
        fetch cur_agcd into rec_bill;
        exit when cur_agcd%notfound;
            v_opbal:=0;v_billamt:=0;v_dbcramt :=0;v_dbamt :=0; v_cramt :=0; v_recamt :=0; v_secamt :=0;v_psecamt :=0;v_csecamt :=0;
            --insert into test1(vstring,vstring2) values(' cir_pur_bill_print_p1 ',rec_bill.billagcd||rec_bill.billdpcd);commit;
            v_opbal :=cir_accounts.cir_agency_opbal(rec_bill.comp_code,rec_bill.unit_code,'',rec_bill.billagcd,rec_bill.billdpcd,v_opdate,'NM',pdateformat,null,null);
            
            select sum(bill_amount) into v_billamt from cir_bill
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and
                      agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and billdt >= v_opdate and billdt<v_frdt;

            select sum(amount) into v_dbcramt from cir_rcphdr
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and achd='NM' and doctype<>'BIL' and 
                      recptdt>= v_opdate and recptdt<v_frdt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd;

            select sum(amount) into v_dbamt from cir_rcphdr
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and achd='NM' and doctype not in('RCR','UCN') and
                      recptdt>= v_frdt and recptdt<=v_todt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and amount>0;

            select sum(amount) into v_cramt from cir_rcphdr
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and achd='NM' and doctype not in('RCR','UCN') and
                      recptdt>= v_frdt and recptdt<=v_todt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and amount<0;

            select sum(amount) into v_recamt from cir_rcphdr
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and achd='NM' and doctype='RCR' and 
                      recptdt>= v_frdt and recptdt<=v_todt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd;

            v_opbal:=nvl(v_opbal,0)+nvl(v_billamt,0)+nvl(v_dbcramt,0);
            v_dbcramt:=0;
            ----for secutiry opening balance---
            v_dbcramt :=cir_accounts.cir_agency_opbal(rec_bill.comp_code,rec_bill.unit_code,'',rec_bill.billagcd,rec_bill.billdpcd,v_opdate,'SC',pdateformat,null,null);
            
            select sum(amount) into v_psecamt from cir_rcphdr
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and achd='SC' and
                      recptdt>=v_opdate and recptdt<=v_todt and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd;
            
            v_secamt:=nvl(v_dbcramt,0)+nvl(v_psecamt,0);
            
            select abs(SUM(sec_amt)) into v_csecamt from cir_bill_print
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and 
                supply_date >= trunc(v_frdt,'mm') and supply_date<=v_todt and cur_sessionid=userenv('sessionid');
            
            v_secamt:=nvl(v_secamt,0)-nvl(v_csecamt,0);
            v_dbcramt:=0;
            select SUM(nvl(gross_amt,0)-nvl(comm_amt,0)+nvl(sur_amt,0)-nvl(return_amt,0)+nvl(sec_amt,0)) into v_dbcramt from cir_bill_print
                where comp_code=rec_bill.comp_code and unit_code=rec_bill.unit_code and agcd=rec_bill.billagcd and dpcd=rec_bill.billdpcd and 
                supply_date >= trunc(v_frdt,'mm') and supply_date<v_frdt and cur_sessionid=userenv('sessionid');            
            
            v_opbal:=nvl(v_opbal,0)+nvl(v_dbcramt,0);
            
            insert into cir_temp_outstanding(comp_code,unit_code,dt,agname,publ_code,
            agcd,dpcd,dn_amt,cn_amt,rec_amt,op_amt,return_amt)
            values(rec_bill.comp_code,rec_bill.unit_code,v_todt,cir_get_name.cir_agname(rec_bill.comp_code,rec_bill.unit_code,rec_bill.billagcd,rec_bill.billdpcd,1,pdateformat,null,null) ,rec_bill.publ,
            rec_bill.billagcd,rec_bill.billdpcd,v_dbamt,v_cramt,v_recamt,v_opbal,v_secamt);

            v_dt:=rec_bill.frdt;
            Loop
                if v_dt>rec_bill.todt then
                    exit;
                end if;
                open cur_edtn;
                loop
                    fetch cur_edtn into rec_edtn;
                    exit when cur_edtn%notfound;
                    Open cur_dbksup;
                    fetch cur_dbksup into rec_dbksup;
                    if cur_dbksup%notfound then

                        v_comm_rate   :=0;v_sur_rate    :=0;
                        v_gross_amt   :=0;
                        v_comm_amt    :=0;
                        v_sur_amt     :=0;

                        insert into cir_bill_print(comp_code,unit_code,billno,billdt,publ_code,edtn_code,agcd,dpcd,
                                                   supply_date,supply_copy,supply_rate,gross_amt,comm_rate,comm_amt,sur_rate,sur_amt,
                                                   return_copy,return_amt,cur_sessionid)
                         values(rec_bill.comp_code,rec_bill.unit_code,rec_bill.billno,rec_bill.billdt,rec_dbksup.publ,rec_dbksup.edtn,rec_bill.billagcd,rec_bill.billdpcd,
                                     v_dt,0,0,v_gross_amt,v_comm_rate,v_comm_amt,v_sur_rate,v_sur_amt,
                                     nvl(v_return_copy,0),nvl(v_return_amt,0),userenv('sessionid'));
                                     v_return_copy:=0;v_return_amt:=0;
                    else
                        v_comm_rate   :=0;v_sur_rate    :=0;

                        v_gross_amt   :=nvl((rec_dbksup.supply_copy*rec_dbksup.copy_rate),0);
                        v_comm_amt    :=nvl((rec_dbksup.supply_copy*(v_comm_rate/100)),0);
                        v_sur_amt     :=nvl((rec_dbksup.supply_copy*v_sur_rate),0);

                        insert into cir_bill_print(comp_code,unit_code,billno,billdt,publ_code,edtn_code,agcd,dpcd,
                                                   supply_date,supply_copy,supply_rate,gross_amt,comm_rate,comm_amt,sur_rate,sur_amt,
                                                   return_copy,return_amt,cur_sessionid)
                         values(rec_bill.comp_code,rec_bill.unit_code,rec_bill.billno,rec_bill.billdt,rec_dbksup.publ,rec_dbksup.edtn,rec_bill.billagcd,rec_bill.billdpcd,
                                     rec_dbksup.supdate,rec_dbksup.supply_copy,rec_dbksup.copy_rate,v_gross_amt,v_comm_rate,v_comm_amt,v_sur_rate,v_sur_amt,
                                     nvl(v_return_copy,0),nvl(v_return_amt,0),userenv('sessionid'));
                                     v_return_copy:=0;v_return_amt:=0;
                    end if;
                Close cur_dbksup;
            
                Open cur_unsold;
                loop
                    fetch cur_unsold into rec_unsold;
                    exit when cur_unsold%notfound;
                    
                        insert into cir_bill_return_print(comp_code,unit_code,billno,billdt,publ_code,edtn_code,agcd,dpcd,
                                                   receipt_date,return_copy,supply_rate,gross_amt,comm_rate,comm_amt,sur_rate,sur_amt,
                                                   cur_sessionid)
                         values(rec_bill.comp_code,rec_bill.unit_code,rec_bill.billno,rec_bill.billdt,rec_edtn.publ,rec_edtn.edtn,rec_bill.billagcd,rec_bill.billdpcd,
                                     rec_unsold.recptdt,rec_unsold.return_copy,rec_unsold.copy_rate,rec_unsold.return_gross,0,rec_unsold.return_comm,0,0,
                                     userenv('sessionid'));
                                     
                                     v_return_copy:=nvl(v_return_copy,0)+nvl(rec_unsold.return_copy,0);
                                     
                                     rec_unsold.recptdt:=null;rec_unsold.return_copy:=null;rec_unsold.copy_rate:=null;
                        
                end loop;
                Close cur_unsold;
                if nvl(v_return_copy,0)=0 then
                    insert into cir_bill_return_print(comp_code,unit_code,billno,billdt,publ_code,edtn_code,agcd,dpcd,
                                               receipt_date,return_copy,supply_rate,gross_amt,comm_rate,comm_amt,sur_rate,sur_amt,
                                               cur_sessionid)
                     values(rec_bill.comp_code,rec_bill.unit_code,rec_bill.billno,rec_bill.billdt,rec_edtn.publ,rec_edtn.edtn,rec_bill.billagcd,rec_bill.billdpcd,
                                 v_dt,0,0,0,0,0,0,0,
                                 userenv('sessionid'));     
                        
                end if;
                v_return_copy:=0;v_return_amt:=0;            
              end loop;
              close cur_edtn;
        v_dt:=v_dt+1;
        End Loop;--close loop for date

        End Loop;
        Close cur_agcd;    
        commit;
        
        open p_accounts for
        select z.comp_code comp_code,z.unit_code unit_code,z.publ publ,z.edtn edtn,z.edition_name edition_name,
        z.edtn_rate edtn_rate,z.billagcd billagcd,z.billdpcd billdpcd,
        z.agency_name agency_name,y.addr1||' Tal.'||initcap(cir_get_taluka(y.comp_code,y.tehsil_taluka))||' Dist.'||initcap(cir_get_dist(y.comp_code,y.state_code,y.dist_code))||' Pin '||y.pin_code addr,
        z.sun as "SUN",z.mon as "MON",z.tue as "TUE",z.wed as "WED",z.thu as "THU",z.fri as "FRI",z.sat as "SAT",z.total_amt as "TOTAL_AMT"
        from(
        select comp_code,unit_code,publ,edtn,cir_get_name.cir_edtn(comp_code,unit_code,edtn,plangtype,''''||pdateformat||'''','','') edition_name,
        sup_rate edtn_rate,billagcd,billdpcd,
        cir_get_name.cir_agname(comp_code,unit_code,billagcd,billdpcd,plangtype,''''||pdateformat||'''',null,null) agency_name,
        sum(p01) as "SUN",sum(p02) as "MON",sum(p03) as "TUE",sum(p04) as "WED",sum(p05) as "THU",sum(p06) as "FRI",sum(p07) as "SAT",sum(gross_amt) as "TOTAL_AMT"
        from(select d.comp_code comp_code,d.unit_code unit_code,d.publ_code publ,d.edtn_code edtn,d.agcd billagcd,d.dpcd billdpcd,
        d.supply_date supdate,d.supply_rate sup_rate,decode(to_char(d.supply_date,'DY'),'SUN',sum(nvl(d.supply_copy,0))) p01,decode(to_char(d.supply_date,'DY'),'MON',sum(nvl(d.supply_copy,0))) p02,decode(to_char(d.supply_date,'DY'),'TUE',sum(nvl(d.supply_copy,0))) p03,
        decode(to_char(d.supply_date,'DY'),'WED',sum(nvl(d.supply_copy,0))) p04,decode(to_char(d.supply_date,'DY'),'THU',sum(nvl(d.supply_copy,0))) p05,decode(to_char(d.supply_date,'DY'),'FRI',sum(nvl(d.supply_copy,0))) p06,
        decode(to_char(d.supply_date,'DY'),'SAT',sum(nvl(d.supply_copy,0))) p07,
        sum(nvl(d.supply_copy,0)*nvl(d.supply_rate,0)) gross_amt
        from cir_agmast m,cir_bill_print d 
        where d.comp_code=pcompcode and d.comp_code=m.comp_code and d.unit_code=m.unit and d.unit_code=punitcode and d.supply_date >= v_frdt and d.supply_date <=v_todt and 
        m.agcd=d.agcd and m.dpcd=d.dpcd and d.agcd=nvl(pbillagcd,d.agcd) and d.dpcd=nvl(pbilldpcd,d.dpcd) and (m.ag_type=pagencytype or pagencytype is null) and 
        (m.ag_class=pagencyclass or pagencyclass is null) and (m.dist_code=pdistcode or pdistcode is null) and (m.tehsil_taluka=ptaluka or ptaluka is null) and --d.supply_copy>0 and 
        d.cur_sessionid=userenv('sessionid')
        group by d.comp_code,d.unit_code,d.publ_code,d.edtn_code,d.agcd,d.dpcd,d.supply_date,d.supply_rate)
        group by comp_code,unit_code,publ,edtn,billagcd,billdpcd,sup_rate) z,cir_agmast y
        where z.comp_code=y.comp_code and z.unit_code=y.unit and z.billagcd=y.agcd and z.billdpcd=y.dpcd
        order by comp_code,unit_code,billagcd,billdpcd,agency_name,publ,edtn;
    
        open p_accounts1 for
        select comp_code,unit_code,dt billdt,agname billno,publ_code publ,agcd,dpcd,
        dn_amt as "Debit Amt",cn_amt as "Credit Amt",nvl(dn_amt,0)+nvl(cn_amt,0) as "Db_Cr Amt",ABS(rec_amt) as "Receipt Amount",op_amt as "Previous Balance",
        return_amt as "Deposit Balance"
        from cir_temp_outstanding
        order by comp_code,unit_code,agcd,dpcd,agname;    

        open p_accounts2 for 
        select distinct h.comp_code comp_code,h.unit_code unit_code,h.doctype||'\'||h.recptno recptno,h.recptdt recptdt,h.agcd agcd,h.dpcd dpcd,abs(h.amount) amount,
        case when h.doctype='RCR' then (select "Payment_Mode_Name" from payment_mode_mast where "Pay_Mode_Code"=h.reason)
        else (select reason_name from cir_reason_mst where comp_code=h.comp_code and reason_code=h.reason) end as reason,
        h.remark remark,h.chno chno,h.chdt chdt,(select bankname from cir_bank_mast where comp_code= h.comp_code and bankcode=h.chbank) bank_name
        from cir_rcphdr h,cir_bill_print d
        where h.comp_code=d.comp_code and h.comp_code=pcompcode and h.unit_code=punitcode  and h.unit_code=d.unit_code and h.recptdt >= v_frdt and h.recptdt<=v_todt and h.achd='NM' and h.amount<>0 and
        h.doctype in('RCR') and h.agcd=d.agcd and h.dpcd=d.dpcd and h.agcd=nvl(pbillagcd,h.agcd) and h.dpcd=nvl(pbilldpcd,h.dpcd) and h.branch_code=nvl(pbranchcode,h.branch_code) and 
        d.supply_date >= v_frdt and d.supply_date<=v_todt and d.cur_sessionid=userenv('sessionid')
        order by agcd,dpcd,recptdt,recptno;
    
        open p_accounts3 for 
        select distinct d.comp_code comp_code,d.unit_code unit_code,d.agcd billagcd,d.dpcd billdpcd,
        cir_get_name.cir_agname(d.comp_code,d.unit_code,d.agcd,d.dpcd,plangtype,''||pdateformat||'',null,null) agency_name,
        sum(d.gross_amount) gross_amt,sum(d.comm_amt) comm_amt,sum(d.bill_amount) bill_amt
        from cir_agmast m,cir_bill_det d
        where d.comp_code=pcompcode and d.comp_code=m.comp_code and d.unit_code=m.unit and d.unit_code=punitcode and 
        d.billdt >= v_frdt and d.billdt <=v_todt and 
        m.agcd=d.agcd and m.dpcd=d.dpcd and d.agcd=nvl(pbillagcd,d.agcd) and d.dpcd=nvl(pbilldpcd,d.dpcd) and 
        (m.ag_type=pagencytype or pagencytype is null) and (m.ag_class=pagencyclass or pagencyclass is null) and (m.dist_code=pdistcode or pdistcode is null) and 
        (m.tehsil_taluka=ptaluka or ptaluka is null) 
        group by d.comp_code,d.unit_code,d.agcd,d.dpcd
        order by d.comp_code,d.unit_code,d.agcd,d.dpcd;
        
        open p_accounts4 for 
        select comp_code,unit_code,publ,edtn,cir_get_name.cir_edtn(comp_code,unit_code,edtn,plangtype,''||pdateformat||'','','') edition_name,sup_rate edtn_rate,billagcd,billdpcd,
        cir_get_name.cir_agname(comp_code,unit_code,billagcd,billdpcd,plangtype,''||pdateformat||'',null,null) agency_name,
        sum(u01) as "SUN_RET",sum(u02) as "MON_RET",sum(u03) as "TUE_RET",sum(u04) as "WED_RET",sum(u05) as "THU_RET",sum(u06) as "FRI_RET",sum(u07) as "SAT_RET",
        sum(return_gross_amt) as "RET_GROSS_AMT",sum(ret_comm_amt) as "RET_COMM_AMT",nvl(sum(return_gross_amt),0)-nvl(sum(ret_comm_amt),0) as "RET_AMT"
        from(select d.comp_code comp_code,d.unit_code unit_code,d.publ_code publ,d.edtn_code edtn,d.agcd billagcd,d.dpcd billdpcd,
        d.receipt_date supdate,d.supply_rate sup_rate,
        decode(to_char(d.receipt_date,'DY'),'SUN',sum(nvl(d.return_copy,0))) u01,decode(to_char(d.receipt_date,'DY'),'MON',sum(nvl(d.return_copy,0))) u02,decode(to_char(d.receipt_date,'DY'),'TUE',sum(nvl(d.return_copy,0))) u03,
        decode(to_char(d.receipt_date,'DY'),'WED',sum(nvl(d.return_copy,0))) u04,decode(to_char(d.receipt_date,'DY'),'THU',sum(nvl(d.return_copy,0))) u05,decode(to_char(d.receipt_date,'DY'),'FRI',sum(nvl(d.return_copy,0))) u06,
        decode(to_char(d.receipt_date,'DY'),'SAT',sum(nvl(d.return_copy,0))) u07,sum(nvl(d.return_copy,0)*nvl(d.supply_rate,0)) return_gross_amt,sum(comm_amt) ret_comm_amt
        from cir_agmast m,cir_bill_return_print d 
        where d.comp_code=pcompcode and d.comp_code=m.comp_code and d.unit_code=m.unit and d.unit_code=punitcode and d.receipt_date >= v_frdt and d.receipt_date <=v_todt and 
        m.agcd=d.agcd and m.dpcd=d.dpcd and d.agcd=nvl(pbillagcd,d.agcd) and d.dpcd=nvl(pbilldpcd,d.dpcd) and (m.ag_type=pagencytype or pagencytype is null) and 
        (m.ag_class=pagencyclass or pagencyclass is null) and (m.dist_code=pdistcode or pdistcode is null) and (m.tehsil_taluka=ptaluka or ptaluka is null) and --d.supply_copy>0 and 
        d.cur_sessionid=userenv('sessionid')
        group by d.comp_code,d.unit_code,d.publ_code,d.edtn_code,d.agcd,d.dpcd,d.receipt_date,d.supply_rate)
        --where (nvl(u01,0)+nvl(u02,0)+nvl(u03,0)+nvl(u04,0)+nvl(u05,0)+nvl(u06,0)+nvl(u07,0))>0
