CREATE OR REPLACE procedure cir_vch_detail_List_bind_p(
       pcompcode       in  varchar2,
       punit           in varchar2,
       pdoctyp         in varchar2,
       pvoucherno      in varchar2,
       precptdt        in varchar2,
       precptno        in varchar2,
       pdateformat     in varchar2,
   p_Accounts      out cur_type_new1.cursor_type) as
   
   pdate     date;

begin
pdate:=to_date(precptdt,''''||pdateformat||'''');
   open p_Accounts for
               select COMP_CODE ,UNIT_CODE ,AGCD  ,DPCD ,
               cir_get_agency(COMP_CODE,UNIT_CODE,AGCD,DPCD) AGENCY
