CREATE OR REPLACE PROCEDURE cir_bill_unadjusted
   (pcompcode           in varchar2,
    pbilldate           in varchar2,
    pbillno             in varchar2,
    puserid             in varchar2,
    pdateformat         in varchar2,
    pextra1             in varchar2,
    pextra2             in varchar2)
IS
    v_billdt                date; 
    v_bill_cheque_bounce    number;
    rec_count               number:=0;
    v_count               number:=0;
    v_outamt                number:=0;
    v_hdramt                number:=0;
        cursor c1 is 
            select * from cir_outmast where COMP_CODE=pcompcode and BILLDT=v_billdt and BILLNO =pbillno and RECPTNO is not null and AMOUNT<0
            order by DOCTYP,RECPTDT,recptno;
     v1 c1%rowtype;
Begin
    v_billdt  := to_date(pbilldate,''''||pdateformat||'''');
        
    open c1;
    loop
        fetch c1 into v1;
        exit when c1%notfound;
                
        select count(*) into v_bill_cheque_bounce from cir_outmast 
            where comp_code=v1.comp_code and billno=v1.billno and billdt=v1.billdt and 
                recptno is not null and amount>0;
                
        if nvl(v_bill_cheque_bounce,0)=0 then--checking for v_bill_cheque_bounce--

            delete from cir_outmast 
                where comp_code=v1.comp_code and doctyp=v1.doctyp and recptno=v1.recptno and recptdt=v1.recptdt and billno=v1.billno and billdt=v1.billdt;

            delete from cir_rcpdet 
                where COMP_CODE=v1.comp_code and DOCTYP=v1.doctyp and RECPTNO=v1.recptno and RECPTDT=v1.recptdt and BILLNO=v1.billno and BILLDT=v1.billdt;
                                                 
            update cir_outmast set amount=nvl(amount,0)+nvl(v1.amount,0)
                where   comp_code      =    v1.comp_code and
                        unit_code           =    v1.unit_code and 
                        doctyp        =    v1.doctyp and 
                        recptno        =    v1.recptno and 
                        recptdt        =    v1.recptdt and 
                        billno is null;
            ---for amount checking---
            select sum(amount) into v_outamt from cir_outmast
            where   comp_code      =    v1.comp_code and
                        unit_code           =    v1.unit_code and 
                        doctyp        =    v1.doctyp and 
                        recptno        =    v1.recptno and 
                        recptdt        =    v1.recptdt;

            select sum(AMOUNT) into v_hdramt from cir_rcphdr
            where   COMP_CODE      =    v1.comp_code and
                    UNIT_CODE          =    v1.unit_code and 
                    DOCTYPE      =    v1.doctyp and 
                    RECPTNO       =    v1.recptno and 
                    RECPTDT       =    v1.recptdt;
            if v_outamt=v_hdramt then                                            
                rec_count:=nvl(rec_count,0)+1;
            end if;
            ---for amount checking---
        end if;
        v_count:=nvl(v_count,0)+1;
        v_bill_cheque_bounce:=0;v_outamt :=0;v_hdramt :=0;
    end loop;
    close c1;
    
    if nvl(rec_count,0)>0 then
        commit;
    else
        rollback;
    end if;
end cir_bill_unadjusted;
/


