CREATE OR REPLACE procedure cir_cn_transfer_process_p(
    pcompcode       in varchar2,
    punitcode       in varchar2,
    pagcode         in varchar2,
    pdepocode       in varchar2,
    pdatefrom       in varchar2,
    pdatetill       in varchar2,
    precptno        in varchar2,
    preason         in varchar2,
    pdn_reason      in varchar2,
    pcn_reason      in varchar2,
    premark         in varchar2,
    puserid         in varchar2,
    pdateformat     in varchar2,
    pextra1         in varchar2,
    pextra2         in varchar2,
    pextra3         in varchar2,
    pextra4         in varchar2,
    pextra5         in varchar2,
    P_Accounts      OUT Cur_Type_New1.cursor_type)
As
    v_frdt      date;
    v_todt      date;
    v_doctype   varchar2(10);
    v_recptdt   date;
    v_dt        date;
    a           int;
    v1_comp_code        varchar2(20);
    v1_unit_code        varchar2(20);
    v1_branch_code      varchar2(20);
    v1_agcd             varchar2(20);
    v1_dpcd             varchar2(20);
    v1_amt              number(15,2);

    v_dn_reason_name    varchar2(200);
    v_cn_reason_name    varchar2(200);
    v_cn_doctype        varchar2(20);
    v_dn_doctype        varchar2(20);
    vcn_ccdp            varchar2(20);
    vcn_rcdp            varchar2(20);
    vdn_ccdp            varchar2(20);
    vdn_rcdp            varchar2(20);
        
    cursor c1 is
        select comp_code,unit_code,branch_code,agcd,dpcd,abs(sum(amount)) amt from cir_rcphdr
            where comp_code=pcompcode and unit_code=nvl(punitcode,unit_code) and 
                agcd=pagcode and dpcd=pdepocode and recptdt between v_frdt and v_todt and 
                reason=preason and doctype='CN' and achd='NM' and nvl(trf_note_type,'N')<>'Y'
                and recptno in(select edition_name from result)
                group by comp_code,unit_code,branch_code,agcd,dpcd;
                
    v_remark    varchar2(500);
    v_recptno   varchar2(50);
    v_rec_no     number(10);  
Begin

    v_frdt      :=to_date(pdatefrom,''''||pdateformat||'''');
    v_todt      :=to_date(pdatetill,''''||pdateformat||'''');
    v_recptdt   :=to_date(trunc(sysdate),''''||pdateformat||'''');

    a:=Fn_Splitfield(precptno,',');
        
    v_cn_doctype:='CN';
    v_dn_doctype:='DN';
        
    open c1;
    loop
        fetch c1 into v1_comp_code,v1_unit_code,v1_branch_code,v1_agcd,v1_dpcd,v1_amt;
        exit when c1%notfound;
        
        Begin
            select distinct reason_name into v_dn_reason_name from cir_reason_mst where reason_code=pdn_reason;
        Exception when others then
            v_dn_reason_name:=null;
        End;
        
        Begin
            select distinct reason_name into v_cn_reason_name from cir_reason_mst where reason_code=pcn_reason;
        Exception when others then
            v_cn_reason_name:=null;
        End;
        
        /************************************for Security Deposit**************************/
        Begin
            select max(to_number(substr(recptno,-8)))+1 into v_rec_no from cir_rcphdr 
                where comp_code=v1_comp_code  and doctype=v_cn_doctype and to_char(recptdt,'yymm')=to_char(v_recptdt,'yymm') and 
                    branch_code=v1_branch_code;
            Exception When Others Then
                v_rec_no:=null;
        End;
            
        If nvl(v_rec_no,0)=0 Then
          v_recptno:=v1_branch_code||'-'||to_char(v_recptdt,'yymm')||'0001';
        Else
          v_recptno:=v1_branch_code||'-'||lpad(v_rec_no,8,'0');
        End if;
        
          insert into cir_rcphdr(comp_code,unit_code,agcd,dpcd,doctype,
                                 recptno,recptdt,amount,reason,remark,
                                 achd,voucherno,voucherdt,userid,creation_date,branch_code,ccdp,rcdp)
                          values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_cn_doctype,
                                 v_recptno,v_recptdt,-1*v1_amt,pcn_reason,v_cn_reason_name,
                                 'SC',v_recptno,v_recptdt,puserid,sysdate,v1_branch_code,vcn_ccdp,vcn_rcdp);

          insert into cir_rcpdet(comp_code,unit_code,agcd,dpcd,doctyp,publ,
                                 recptno,recptdt,payfor,achd,amount,
                                 reason,remark,voucherno,voucherdt,usrid,
                                 creation_date,branch_code)
                          values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_cn_doctype,null,
                                 v_recptno,v_recptdt,v1_unit_code,'SC',-1*v1_amt,
                                 pcn_reason,v_cn_reason_name,v_recptno,v_recptdt,puserid,
                                 sysdate,v1_branch_code);

          insert into cir_outmast(comp_code,unit_code,agcd,dpcd,doctyp,publ,
                                  recptno,recptdt,achd,amount,reason,
                                  remark,voucherno,voucherdt,usrid,creation_date,branch_code)
                           values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_cn_doctype,null,
                                  v_recptno,v_recptdt,'SC',-1*v1_amt,pcn_reason,
                                  v_cn_reason_name,v_recptno,v_recptdt,puserid,sysdate,v1_branch_code);                               

        /************************************Credit Note for Security Deposit**************************/

        /************************************Debit Note For Normal**************************/
        Begin
            select max(to_number(substr(recptno,-8)))+1 into v_rec_no from cir_rcphdr 
                where comp_code=v1_comp_code  and doctype=v_dn_doctype and to_char(recptdt,'yymm')=to_char(v_recptdt,'yymm') and 
                    branch_code=v1_branch_code;
            Exception When Others Then
                v_rec_no:=null;
        End;
            
        If nvl(v_rec_no,0)=0 Then
          v_recptno:=v1_branch_code||'-'||to_char(v_recptdt,'yymm')||'0001';
        Else
          v_recptno:=v1_branch_code||'-'||lpad(v_rec_no,8,'0');
        End if;
        
          insert into cir_rcphdr(comp_code,unit_code,agcd,dpcd,doctype,
                                 recptno,recptdt,amount,reason,remark,
                                 achd,voucherno,voucherdt,userid,creation_date,branch_code,ccdp,rcdp)
                          values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_dn_doctype,
                                 v_recptno,v_recptdt,v1_amt,pdn_reason,v_dn_reason_name,
                                 'NM',v_recptno,v_recptdt,puserid,sysdate,v1_branch_code,vdn_ccdp,vdn_rcdp);

          insert into cir_rcpdet(comp_code,unit_code,agcd,dpcd,doctyp,publ,
                                 recptno,recptdt,payfor,achd,amount,
                                 reason,remark,voucherno,voucherdt,usrid,
                                 creation_date,branch_code)
                          values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_dn_doctype,null,
                                 v_recptno,v_recptdt,v1_unit_code,'NM',v1_amt,
                                 pdn_reason,v_dn_reason_name,v_recptno,v_recptdt,puserid,
                                 sysdate,v1_branch_code);
                                 
          insert into cir_outmast(comp_code,unit_code,agcd,dpcd,doctyp,publ,
                                  recptno,recptdt,achd,amount,reason,
                                  remark,voucherno,voucherdt,usrid,creation_date,branch_code)
                           values(v1_comp_code, v1_unit_code,v1_agcd, v1_dpcd,v_dn_doctype,null,
                                  v_recptno,v_recptdt,'NM',v1_amt,pdn_reason,
                                  v_dn_reason_name,v_recptno,v_recptdt,puserid,sysdate,v1_branch_code);                               

        /************************************Debit Note For Normal**************************/
                                  
        update cir_rcphdr set trf_note_type='Y'
            where comp_code=v1_comp_code and unit_code=v1_unit_code and 
                agcd=v1_agcd and dpcd=v1_dpcd and recptdt between v_frdt and v_todt and 
                reason=preason and doctype='CN' and achd='NM' and nvl(trf_note_type,'N')<>'Y'
                and branch_code=v1_branch_code
                and recptno in(select edition_name from result);
    end loop;
    close c1;
    commit;
            
    open P_Accounts for
    select sysdate as "CUR_DATE" from dual;
   
End cir_cn_transfer_process_p;
/



