                vvar_sum    :=vvar_sum||','||'sum('||'"'||to_char(v_dt,'Mon/YYYY')||'") "'||to_char(v_dt,'Mon/YYYY')||'"';
                vvar_sum1   :=vvar_sum1||'+"'||to_char(v_dt,'Mon/YYYY')||'"';
