        b.app_dt between '||''''||vfrdt||''''||' and '||''''||vtodt||''''||' group by b.comp_code,b.unit_code,b.app_dt,b.comm_rate)
        group by comp_code,unit_code,comm_rate order by comp_code,unit_code,comm_rate'; 
        
