        group by a.comp_code,a.unit_code,a.taluka_code
        order by comp_code,unit_code,taluka_name;
     end if;

    delete from cir_temp_bill_collection where cur_sessionid=userenv('sessionid');
End cir_area_break_down;
END cir_rep_abc;
